import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { IonicModule } from '@ionic/angular';
import { Ejemplo, ProductsService } from '../services/products.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.page.html',
  styleUrls: ['./product-list.page.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, FormsModule]
})
export class ProductListPage implements OnInit {

  products: Ejemplo[] = [];
  errorMessage: string | null = null;  // Para manejar posibles errores

  productsService = inject(ProductsService);

  async ngOnInit() {
    try {
      const response = await this.productsService.getAll();
      this.products = response.ejemplos;
    } catch (error) {
      console.error('Error al cargar los productos:', error);
      this.errorMessage = 'Hubo un problema al cargar los productos. Inténtalo de nuevo más tarde.';
    }
  }
}

<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button menu="m1"></ion-menu-button>
    </ion-buttons>
    <ion-title>Autos</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Modelos</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-list>
    <ion-list-header>
      <ion-label>AModelos</ion-label>
    </ion-list-header>

    <!-- Si hay un error, mostramos un mensaje -->
    <div *ngIf="errorMessage" class="error-message">
      <p>{{ errorMessage }}</p>
    </div>

    <!-- Si no hay error, mostramos los productos -->
    <ng-container *ngIf="!errorMessage">
      <ng-container *ngFor="let product of products">
        <ion-item>
          <ion-thumbnail slot="start">
            <img [src]="product.Imagen" [alt]="product.Marca">
          </ion-thumbnail>
          <ion-label>
            <h2>{{ product.Marca }}</h2>
            <p>{{ product.Tipo_Vehiculo }} - {{ product.Tipo_Motor }} - {{ product.Color }}</p>
          </ion-label>
        </ion-item>
      </ng-container>
    </ng-container>
  </ion-list>
</ion-content>
